mapper.update:
/* [chn.bhmc.dms.bat.cmm.sms.service.dao.SendSmsMessageDAO.updateSmsDlrAmount] */
UPDATE CM_0101T
   SET SMS_REMAIN_AMOUNT = #{smsRemainAmount}
     , SMS_REMAIN_CNT = #{smsRemainCnt}
 WHERE DLR_CD = #{dlrCd}
   AND DMS_USE_YN = 'Y'

