mapper.select:
/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectVinNoSearchPopupSearchData] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.CAR_ID ,A.VIN_NO ,A.DLR_CD ,A.CARLINE_CD ,FN_GET_CARLINE_NM(A.CARLINE_CD) AS CARLINE_NM ,A.MODEL_CD ,FN_GET_MODEL_TP_NM(A.MODEL_CD, A.OCN_CD) AS MODEL_NM ,A.OCN_CD ,FN_GET_OCN_NM(A.MODEL_CD, A.OCN_CD) AS OCN_NM ,A.EXT_COLOR_CD ,FN_GET_EXT_COLOR_NM(A.EXT_COLOR_CD) AS EXT_COLOR_NM ,A.INT_COLOR_CD ,FN_GET_INT_COLOR_NM(A.INT_COLOR_CD) AS INT_COLOR_NM ,A.CAR_STAT_CD ,A.PLT_GI_DT ,A.CUST_SALE_DT ,A.JEONGPAN_NO ,A.VINM_F20010 ,B.MS_PRC AS WS_PRC FROM SA_0121T A LEFT OUTER JOIN SA_0110T B ON A.MODEL_CD = B.MODEL_CD AND A.OCN_CD = B.OCN_CD WHERE A.CAR_STAT_CD IN ('50','60','70') AND A.VIN_NO LIKE '%'||#{sVinNo}||'%' AND A.NCAR_DSTIN_CD = 'N' ) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.sal.rcp.service.dao.SalesVhclCbinRcitDAO.selectSecondDealerSearchCnt] */
