mapper.select:
     SELECT count(*)
     FROM (    
        select ROWNUM AS RNUM , T1.* 
		from(
		    select invoice.DLR_CD,(select DLR_NM from CM_0101T where DLR_CD = invoice.DLR_CD) as DLR_NM,invoice.INVC_NO,
		           invoice.RECEIPT_TP,invoice.RECIP_DT,invoice.FAIL_DT,pack.invc_Dt,pack.invc_Claim_Tot_Amt,pack.ddct_Tot_Amt,
		           pack.confirm_Tot_Amt,invoice.TRSF_NO,invoice.EXPS_CMP_NM,invoice.TRSF_TP,invoice.FAIL_MSG,invoice.SENDER_NM,
		           invoice.SENDER_TELNO,invoice.SENDER_ADDR
		    from SE_0960T invoice 
		    left join se_0930t pack on invoice.INVC_NO=pack.INVC_NO
		    where 1 = 1

            AND invoice.DLR_CD = #{sdlrCd}


            AND DLR_NM = #{sdlrNm}


            AND invoice.INVC_NO = #{sinvcNo}


            AND pack.invc_Dt >= #{sinvcsFromDt}


            AND pack.invc_Dt <= #{sinvcsToDt}+1


            AND invoice.RECEIPT_TP = #{sreceiptTp}


            AND invoice.TRSF_TP = #{strsfTp}

		    ORDER BY pack.invc_Dt desc
		) T1


              AND ROWNUM <= #{lastIndex}


    )

            RNUM > #{firstIndex}

     SELECT *
     FROM (    
        select ROWNUM AS RNUM , T1.* 
		from(
		    select invoice.DLR_CD,(select DLR_NM from CM_0101T where DLR_CD = invoice.DLR_CD) as DLR_NM,invoice.INVC_NO,
		           invoice.RECEIPT_TP,invoice.RECIP_DT,invoice.FAIL_DT,pack.invc_Dt,pack.invc_Claim_Tot_Amt,pack.ddct_Tot_Amt,
		           pack.confirm_Tot_Amt,invoice.TRSF_NO,invoice.EXPS_CMP_NM,invoice.TRSF_TP,invoice.FAIL_MSG,invoice.FAIL_REMARK,
		           invoice.SENDER_NM,invoice.SENDER_TELNO,invoice.SENDER_ADDR,invoice.CANCEL_YN
		    from SE_0960T invoice 
		    left join se_0930t pack on invoice.INVC_NO=pack.INVC_NO
		    where 1 = 1

            AND invoice.DLR_CD = #{sdlrCd}


            AND DLR_NM = #{sdlrNm}


            AND invoice.INVC_NO = #{sinvcNo}


            AND pack.invc_Dt >= #{sinvcsFromDt}


            AND pack.invc_Dt <= #{sinvcsToDt}+1


            AND invoice.RECEIPT_TP = #{sreceiptTp}


            AND invoice.TRSF_TP = #{strsfTp}

		    ORDER BY pack.invc_Dt desc
		) T1


              AND ROWNUM <= #{lastIndex}


    )

            RNUM > #{firstIndex}

     SELECT *
     FROM (	
    	select ROWNUM AS RNUM , T1.* 
		from(
	    	select 
			  t.RECEIPT_CD,t.BUY_NM,t.TAXPAYER_ID_CD,
			  t.RECEIPT_ISS_DT,t.SUM_AMT,t.REMARK
			from SA_0502T t
			where t.SETTLEMENT_NO = #{settlementNo}
    	) T1


              AND ROWNUM <= #{lastIndex}


    )

            RNUM > #{firstIndex}

     SELECT count(*)
     FROM (	
    	select ROWNUM AS RNUM , T1.* 
		from(
	    	select 
			  t.RECEIPT_CD,t.BUY_NM,t.TAXPAYER_ID_CD,
			  t.RECEIPT_ISS_DT,t.SUM_AMT,t.REMARK
			from SA_0502T t
			where t.SETTLEMENT_NO = #{settlementNo}
    	) T1


              AND ROWNUM <= #{lastIndex}


    )

            RNUM > #{firstIndex}

mapper.update:
    		UPDATE SE_0960T SET 
	    		RECEIPT_TP 		= #{receiptTp}
	    	  , TRSF_TP    		= #{trsfTp}
	    	  , FAIL_DT    		= SYSDATE
	    	  , FAIL_CNT   		= FAIL_CNT+1
	    	  , FAIL_MSG   		= #{failMsg}
	    	  , FAIL_REMARK 	= #{failRemark}
	    	  , TRSF_NO    		= #{trsfNo}
	    	  , EXPS_CMP_NM 	= #{expsCmpNm}
	    	  , SENDER_NM  		= #{senderNm}
	    	  , SENDER_TELNO 	= #{senderTelno}
    		WHERE 1=1
    		AND INVC_NO 	= #{invcNo}

mapper.select:
select invoices.DLR_CD, invoices.DLR_NM,invoices.INVC_NO, (select CMM_CD_NM from CM_0303T where CMM_CD = invoices.RECEIPT_TP and CMM_GRP_CD='SEF004') as RECEIPT_TP, invoices.RECIP_DT,invoices.FAIL_DT,invoices.invc_Dt,invoices.invc_Claim_Tot_Amt,invoices.ddct_Tot_Amt, invoices.confirm_Tot_Amt,invoices.TRSF_NO,invoices.EXPS_CMP_NM, (select CMM_CD_NM from CM_0303T where CMM_CD = invoices.TRSF_TP and CMM_GRP_CD='SEF005') as TRSF_TP, (select CMM_CD_NM from CM_0303T where CMM_CD = invoices.FAIL_MSG and CMM_GRP_CD='SEF006') as FAIL_MSG,invoices.FAIL_REMARK, invoices.SENDER_NM,invoices.SENDER_TELNO,invoices.SENDER_ADDR, t.RECEIPT_CD,t.BUY_NM,t.TAXPAYER_ID_CD, t.RECEIPT_ISS_DT,t.SUM_AMT,t.REMARK from (select invoice.DLR_CD,(select DLR_NM from CM_0101T where DLR_CD = invoice.DLR_CD) as DLR_NM,invoice.INVC_NO, invoice.RECEIPT_TP,invoice.RECIP_DT,invoice.FAIL_DT,pack.invc_Dt,pack.invc_Claim_Tot_Amt,pack.ddct_Tot_Amt, pack.confirm_Tot_Amt,invoice.TRSF_NO,invoice.EXPS_CMP_NM,invoice.TRSF_TP,invoice.FAIL_MSG,invoice.FAIL_REMARK, invoice.SENDER_NM,invoice.SENDER_TELNO,invoice.SENDER_ADDR from SE_0960T invoice left join se_0930t pack on invoice.INVC_NO=pack.INVC_NO where 1 = 1
AND invoice.DLR_CD = #{sdlrCd}
AND DLR_NM = #{sdlrNm}
AND invoice.INVC_NO = #{sinvcNo}
AND pack.invc_Dt >= #{sinvcsFromDt}
AND pack.invc_Dt <= #{sinvcsToDt}+1
AND invoice.RECEIPT_TP = #{sreceiptTp}
AND invoice.TRSF_TP = #{strsfTp}
) invoices left join SA_0502T t on t.SETTLEMENT_NO = invoices.INVC_NO
